graphql.query:
query GetAllTests(
  $jql: String,
  $limit: Int,
  $start: Int
) {
  getTests(
    jql: $jql,
    limit: $limit,
    start: $start
  ) {
    total
    start
    limit
    results {
      issueId
      testType {
        name
        kind
      }
      folder {
        path
      }
      jira(fields: ["key", "summary", "labels", "status", "priority", "components", "assignee"])
    }
  }
}

