}

mutation UpdateTestLabels(
  $issueId: String!,
  $labels: [String!]!
) {
  updateTest(
    issueId: $issueId,
    jira: {
      update: {
        labels: [
          { set: $labels }
        ]
      }
    }
  ) {
    test {
      issueId
      jira(fields: ["key", "summary", "labels"])
    }
    warnings
  }
}

mutation AddLabelsToTest(
  $issueId: String!,
  $labelsToAdd: [String!]!
) {
  updateTest(
    issueId: $issueId,
    jira: {
      update: {
        labels: [
          { add: $labelsToAdd[0] },
          { add: $labelsToAdd[1] }
        ]
      }
    }
  ) {
    test {
      issueId
      jira(fields: ["key", "labels"])
    }
  }
}

mutation UpdateTestDescription(
  $issueId: String!,
  $summary: String!,
  $description: String!
) {
  updateTest(
    issueId: $issueId,
    jira: {
      fields: {
        summary: $summary,
        description: $description
      }
    }
  ) {
    test {
      issueId
      jira(fields: ["key", "summary", "description"])
    }
    warnings
  }
}

mutation AddTestStep(
  $issueId: String!,
  $action: String!,
  $data: String,
  $result: String!
) {
  addTestStep(
    issueId: $issueId,
    action: $action,
    data: $data,
    result: $result
  ) {
    id
    action
    data
    result
    warnings
  }
}

