mutation CreateManualTest(
  $jira: JSON!,
  $testType: UpdateTestTypeInput,
  $steps: [CreateStepInput!],
  $folderPath: String
) {
  createTest(
    jira: $jira,
    testType: $testType,
    steps: $steps,
    folderPath: $folderPath
  ) {
    test {
      issueId
      testType { name }
      steps {
        id
        action
        data
        result
      }
      folder { path }
      jira(fields: ["key", "labels", "priority", "summary"])
    }
    warnings
  }
}

